import axios from "axios";
const BASE_URL = "http://localhost:5000/api";

export const getArticles = () => axios.get(`${BASE_URL}/articles`);
export const searchArticles = (q) => axios.get(`${BASE_URL}/articles/search?q=${q}`);
export const getArticle = (id) => axios.get(`${BASE_URL}/articles/${id}`);
export const createArticle = (formData) => axios.post(`${BASE_URL}/articles`, formData);
export const updateArticle = (id, formData) => axios.put(`${BASE_URL}/articles/${id}`, formData);
export const deleteArticle = (id) => axios.delete(`${BASE_URL}/articles/${id}`);
