import React, { useState, useEffect } from "react";
import { createArticle, getArticle, updateArticle } from "../api";
import { useNavigate, useParams, Link } from "react-router-dom";
import Sidebar from "../components/Sidebar";


function ArticleForm() {
  const navigate = useNavigate();
  const { id } = useParams();

  const [form, setForm] = useState({
    title: "",
    category: "",
    content: "",
    status: "Draft",
  });
  const [thumbnail, setThumbnail] = useState(null);

  useEffect(() => {
    if (id) {
      getArticle(id).then((res) => {
        setForm(res.data);
      });
    }
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFile = (e) => {
    setThumbnail(e.target.files[0]);
  };

  const handleSubmit = async (status) => {
    const formData = new FormData();
    formData.append("title", form.title);
    formData.append("category", form.category);
    formData.append("content", form.content);
    formData.append("status", status);
    if (thumbnail) formData.append("thumbnail", thumbnail);

    if (id) {
      await updateArticle(id, formData);
    } else {
      await createArticle(formData);
    }
    navigate("/admin/manage");
  };

  return (
    <div className="admin-page">
      <div className="sidebar">
        <Link to="/admin">Dashboard</Link>
        <Link to="/admin/manage">Kelola Artikel</Link>
        <Link to="/">Baca Artikel</Link>
      </div>
      <div className="dashboard">
        <h2>{id ? "Edit" : "Tambah"} Artikel</h2>
        <input name="title" placeholder="Judul Artikel" value={form.title} onChange={handleChange} />
        <select name="category" value={form.category} onChange={handleChange}>
          <option value="">Pilih Kategori</option>
          <option value="Tips">Tips</option>
          <option value="Resep">Resep</option>
          <option value="Nutrisi">Nutrisi</option>
        </select>
        <input type="file" onChange={handleFile} />
        <textarea name="content" rows="10" placeholder="Isi Artikel" value={form.content} onChange={handleChange} />
        <div className="buttons">
          <button onClick={() => navigate("/admin/manage")} className="cancel">Batal</button>
          <button onClick={() => handleSubmit("Draft")} className="draft">Draft</button>
          <button onClick={() => handleSubmit("Published")} className="publish">Publish</button>
        </div>
      </div>
    </div>
  );
}

export default ArticleForm;
