import React, { useState, useEffect } from "react";
import { getArticles } from "../api";
import { Link } from "react-router-dom";

function HomePage() {
  const [articles, setArticles] = useState([]);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchArticles();
  }, [category, search]);

  const fetchArticles = async () => {
    try {
      setLoading(true);
      let res;

      // Gunakan pencarian backend jika search aktif
      if (search.trim() !== "") {
        res = await getArticles({ params: { q: search } });
      } else if (category) {
        res = await getArticles({ params: { category } });
      } else {
        res = await getArticles();
      }

      setArticles(res.data);
    } catch (err) {
      console.error("Gagal memuat artikel", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      <div className="navbar">
        <input
          type="text"
          placeholder="Cari artikel..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-input"
        />

        <div className="category">
          {["Semua", "Tips", "Resep", "Nutrisi"].map((cat) => (
            <button
              key={cat}
              className={category === (cat === "Semua" ? "" : cat) ? "active" : ""}
              onClick={() => setCategory(cat === "Semua" ? "" : cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        <Link to="/admin" className="admin-link">Panel Admin</Link>
      </div>

      <div className="article-list">
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "2rem" }}>Memuat artikel...</p>
        ) : articles.length === 0 ? (
          <p style={{ textAlign: "center", marginTop: "2rem", color: "gray" }}>
            Tidak ada hasil yang ditemukan.
          </p>
        ) : (
          articles.map((article) => (
            <Link to={`/artikel/${article.id}`} key={article.id}>
              <div className="article-card">
                <img
                  src={`http://localhost:5001/uploads/${article.thumbnail}`}
                  alt={article.title}
                />
                <h4>{article.title}</h4>
                <p>{Math.ceil(article.content.length / 500)} menit membaca</p>
              </div>
            </Link>
          ))
        )}
      </div>
    </div>
  );
}

export default HomePage;
